import { Plugin } from 'vuepress/core';

/**
 * Options for @vuepress/plugin-google-analytics
 */
interface GoogleAnalyticsPluginOptions {
    /**
     * The Measurement ID of Google Analytics 4, which should start with `'G-'`.
     */
    id: string;
    /**
     * Enable debug mode.
     */
    debug?: boolean;
}

declare const googleAnalyticsPlugin: (options: GoogleAnalyticsPluginOptions) => Plugin;

export { googleAnalyticsPlugin };
